mapper.select:
SELECT p.POST_ID, p.TITLE, p.USER_ID, p.CREATED_DATE, p.CONTENT, p.UPDATED_DATE, p.CATEGORY_CODE, p.VIEW_COUNT,
u.USER_NICKNAME, ( SELECT COUNT(l.POST_ID) FROM TB_COMMUNITY_LIKE l WHERE l.POST_ID = p.POST_ID ) AS LIKE_COUNT, p.IS_DELETED, p.CREATED_BY, p.UPDATED_BY FROM TB_COMMUNITY_POST p LEFT JOIN TB_USER u ON p.USER_ID = u.USER_ID
ORDER BY p.POST_ID DESC LIMIT #{amount} OFFSET #{skip}

mapper.update:
UPDATE TB_COMMUNITY_POST
SET
    LIKE_COUNT = (
        SELECT COUNT(POST_ID)
        FROM TB_COMMUNITY_LIKE
        WHERE POST_ID = #{postId}
    ),
    UPDATED_DATE = NOW()
WHERE POST_ID = #{postId}

UPDATE tb_community_post
SET view_count = view_count + 1
WHERE post_id = #{postId}
